import { useEffect, useState } from "react";
import api from "../services/api";
import { Link } from "react-router-dom";
import Card from 'react-bootstrap/Card';

export default function ListProduct() {

    const [products, setProducts] = useState([]);
    useEffect(() => {
        getProducts();
    }, []);

    function getProducts() {
        api.get('products/').then(function(response) {
            console.log(response.data.message);
            setProducts(response.data.data);
        });
    }

    const deleteProduct = (id) => {
        api.delete(`products/${id}`).then(function(response){
            console.log(response.data.message);
            getProducts();
        });
    }
    return (
        <div>
            <h1>List Products</h1>
            <table>
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Name</th>
                        <th>Description</th>
                        <th>Price</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {products.map((product, key) =>
                        <Card style={{ width: '100%' }} key={key}>
                            <Card.Body>
                              <Card.Title>{product.name}</Card.Title>
                              <Card.Subtitle className="mb-2 text-muted">R$ {product.description}</Card.Subtitle>
                              <Card.Text>
                              {product.description}
                              </Card.Text>
                              <Card.Link href={`product/${product.id}/edit`}>Edit</Card.Link>
                              <Card.Link onClick={() => deleteProduct(product.id)}>Delete</Card.Link>
                            </Card.Body>
                        </Card>
                    )}

                </tbody>
            </table>
        </div>
    )
}
